def spaw_aple():
    for l in mapa:
        for v in l:
            if v==2:
                v=0

    x_aple,y_aple=random.randint(1,(len(mapa)-2)),random.randint(1,(len(mapa[0])-2))
    mapa[x_aple][y_aple]=2

def map_draw():
    for linha in mapa:
        print('\n')   
        for val in linha:
            if val==1:
                print('#',end='')
            elif val==0:
                print(' ',end='')
            elif val==2:
                print('O',end='')

while True:
    spaw_aple()
    map_draw()
    sleep(1)
